import React from 'react';

export class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: 0 }

    this.handleToddlerButtonClicked = this.handleToddlerButtonClicked.bind(this);
  }

  handleToddlerButtonClicked(event) {
    var randomValue = Math.floor(Math.random() * 8) + 1;
    this.setState({value: randomValue});
  }

  render() {
    return(
      <div>
        <p>{this.state.value}</p>
        <button onClick={this.handleToddlerButtonClicked}>
          Generate Toddler Trait
        </button>
      </div>
    )
  }
}

